function tambah(p1, p2)
{
    return p1 + p2;
}

function kurang(p1, p2)
{
    return p1 - p2;
}

function kali(p1, p2)
{
    return p1 * p2;
}

function bagi(p1, p2)
{
    return p1 / p2;
}

console.log("Hasil Penambahan:", tambah(6, 3));
console.log("Hasil Pengurangan:", kurang(6, 3));
console.log("Hasil Perkalian:", kali(6, 3));
console.log("Hasil Pembagian:", bagi(6, 3));
